import React from 'react';

const Ship = ({ship, pirates}) => {

	let piratenodes = [];

	for(let pirate of pirates){
		if (pirate.Ship === ship.name){
		pirateNodes.push(pirate)
	}}

	return (
		<div className="component">
			<p className="name">
				{props.ship.name}
			</p>
			<p>Pirates:</p>

			<p className="pirates">
				{piratenodes}
			</p>

		</div>
	)
}

export default Ship;
