import React from 'react';

const Ship = ({ship, pirates}) => {

	let pirateNodes = [];

	for(let pirate of pirates){
		console.log(pirate.ship.name)
		console.log(ship.name)
		if (pirate.ship.name === ship.name){

		pirateNodes.push(pirate)
	}}

	console.log(pirateNodes)

	return (
		<div className="component">
			<p className="name">
				{ship.name}
			</p>
			<p>Pirates:</p>

			<p className="pirates">
				{pirateNodes}
			</p>

		</div>
	)
}

export default Ship;
