import React from 'react';
import Ship from "./Ship";

const ShipDetail = ({ ship, handleDelete }) => {

    if (ship) {

        return (
            <div className="component">
                <Ship ship={ship} />
                <p>Pirates:</p>
                <p>{ship.pirates}</p>
                <button onClick={onDelete}>Delete {ship.name}</button>
            </div>
        )
    }

    return (
        <p>Loading.....</p>
    )
}

export default PirateDetail;
